import { useUserStore } from '@/store'

const routes = [
  { 
    path: '/', 
    component: Home,
    meta: { title: '首页' }
  },
  { 
    path: '/login', 
    component: Login,
    meta: { title: '登录' }
  },
  { 
    path: '/student', 
    component: StudentDashboard, 
    meta: { 
      requiresAuth: true, 
      role: 'student',
      title: '学生控制台'
    } 
  },
  { 
    path: '/teacher', 
    component: TeacherDashboard, 
    meta: { 
      requiresAuth: true, 
      role: 'teacher',
      title: '教师控制台'
